from longestPalindromicSubstring import Solution

class Tester:
    def __init__(self):
        self.solution = Solution()

    def test(self, data, testName):
      print("testing : {} : with {}".format(testName, data))
      result = self.solution.longestPalindrome(data)
      print("    Result : {}".format(result))

tester = Tester()
tester.test("abcaba", "right alligned, letter centered simple case")
tester.test("abattk", "left alligned, letter centered simple case")
tester.test("abcabba", "right alligned, in-betwin letter centered simple case")
tester.test("abbattk", "left alligned, in-betwin letter centered simple case")
tester.test(""
, "long case")

tester.test("", "long repetitive case")
